import React, { useState } from 'react';
import './styles/Console.css';
import { useGeneratedCode } from '../GeneratedCodeContext';

function TextCode() {
    const { generatedCode } = useGeneratedCode();
    const [editableCode, setEditableCode] = useState(generatedCode);

    const handleCodeChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setEditableCode(event.target.value);
    };

    return (
        <div className="console">
            <div className="title">Generated Code</div>

            <textarea value={editableCode} onChange={handleCodeChange} />

        </div>
    );
}

export default TextCode;
