import * as Blockly from 'blockly/core';

// Create a custom block called 'if_statement' that represents an if statement.
const ifStatement = {
  'type': 'if_statement',
  'message0': 'if %1 then %2',
  'args0': [
    {
      'type': 'input_value',
      'name': 'CONDITION',
      'check': 'Boolean',
    },
    {
      'type': 'input_statement',
      'name': 'DO',
    },
  ],
  'previousStatement': null,
  'nextStatement': null,
  'colour': 160,
  'tooltip': 'Conditional statement with block to execute if the condition is true.',
  'helpUrl': '',
};

// Create the block definitions for the JSON-only blocks.
// This does not register their definitions with Blockly.
// This file has no side effects!
export const ifStatementBlock = Blockly.common.createBlockDefinitionsFromJsonArray([ifStatement]);
