import React, { useState } from 'react';
import BlocklyComponent from './Blockly';
import LeftBar from './LeftBar';
import './styles/WindowManager.css';


function WindowManager() {
    const [isConsoleVisible, setConsoleVisibility] = useState(true);

    const toggleConsoleVisibility = () => {
        setConsoleVisibility(!isConsoleVisible);
    };

    return (
        <div>
            <div className="main-divider">
                <div className="left-bar">
                    <LeftBar />
                </div>
                <div className={`main-area ${isConsoleVisible ? 'show-right-column' : ''}`}>
                    <div className="blockly-area">
                        <BlocklyComponent />
                    </div>
                    { isConsoleVisible &&
                        <div className="console-area">
                            <button onClick={toggleConsoleVisibility}>Hide Console</button>
                            <div>
                                <h3>Received Data:</h3>
                                <div id="outputText"></div>
                            </div>
                        </div>
                    }
                </div>
            </div>
        </div>
    );
}

export default WindowManager;
