import * as Blockly from 'blockly/core';

const createVariableButton = {
    'type': 'create_int_variable_button',
    'message0': 'Create New Variable',
    'previousStatement': null,
    'nextStatement': null,
    'colour': 230,
  };

  export const createIntVariableButtonBlock = Blockly.common.createBlockDefinitionsFromJsonArray(
    [createVariableButton]
  );
// Create a custom block for declaring an integer variable.
const intVars = {
  'type': 'int_var_declaration',
  'message0': 'Declare int %1 %2',
  'args0': [
    {
      'type': 'field_input',
      'name': 'VAR',
      'text': '',
    },
    {
      'type': 'input_value',
      'name': 'VALUE',
      'check': 'Number',
      'align': 'RIGHT',
    },
  ],
  'previousStatement': null,
  'nextStatement': null,
  'colour': 160,
  'tooltip': '',
  'helpUrl': '',
};

// Create the block definitions for the JSON-only blocks.
export const intVarsBlock = Blockly.common.createBlockDefinitionsFromJsonArray([intVars]);

// Create a Blockly workspace
const workspace = Blockly.inject('blocklyDiv', {
  toolbox: toolboxJSON,
  // Add any other configuration options here
});

// Add event listener for the 'CREATE_VARIABLE' button
workspace.registerButtonCallback('CREATE_VARIABLE', function () {
  const variableName = prompt('Enter the variable name:');
  if (variableName) {
    // Create a new variable using Blockly's createVariable function
    workspace.createVariable(variableName);
  }
});
