import * as Blockly from 'blockly/core';

// Create a custom block called 'if_statement' that represents an if statement.
const ifStatement = {
  'type': 'if_statement',
  'message0': 'if %1 then %2',
  'args0': [
    {
      'type': 'input_value',
      'name': 'CONDITION',
      'check': 'Boolean',
    }
  ],
  'previousStatement': null,
  'nextStatement': null,
  'colour': 160,
  'tooltip': 'Conditional statement with block to execute if the condition is true.',
  'helpUrl': '',
};


export const ifBlock = Blockly.common.createBlockDefinitionsFromJsonArray([ifStatement]);
