import * as Blockly from 'blockly/core';

const createVariableButton = {
    'type': 'create_variable_button',
    'message0': 'Create New Variable',
    'previousStatement': null,
    'nextStatement': null,
    'colour': 230,
  };

  export 

// Create a custom block for declaring an integer variable.
const intVars = {
  'type': 'int_var_declaration',
  'message0': 'Declare int %1 %2',
  'args0': [
    {
      'type': 'field_input',
      'name': 'VAR',
      'text': '',
    },
    {
      'type': 'input_value',
      'name': 'VALUE',
      'check': 'Number',
      'align': 'RIGHT',
    },
  ],
  'previousStatement': null,
  'nextStatement': null,
  'colour': 160,
  'tooltip': '',
  'helpUrl': '',
};

// Create the block definitions for the JSON-only blocks.
export const intVarsBlock = Blockly.common.createBlockDefinitionsFromJsonArray(
    [intVars]);
