import React, { useState, useEffect } from 'react';
import { useBluetooth } from '../BluetoothContext';
import { SendScript } from '../SendScript';
import './styles/TopBar.css';

const TopBar: React.FC = () => {
    const { bluetoothDevice, outputText, connectToDevice, disconnectDevice, sendBluetoothData } = useBluetooth();
    const [inputData, setInputData] = useState('');

    const handleConnect = async () => {
        try {
            await connectToDevice();
        } catch (error) {
            console.error('Error connecting to Bluetooth device:', error);
        }
    };

    const handleDisconnect = async () => {
        try {
            await disconnectDevice();
        } catch (error) {
            console.error('Error disconnecting from Bluetooth device:', error);
        }
    };

    const handleSendData = async () => {
        try {
            if (inputData) {
                await sendBluetoothData(inputData);
                setInputData('');
            }
        } catch (error) {
            console.error('Error sending data to Bluetooth device:', error);
        }
    };

    useEffect(() => {
        if (bluetoothDevice) {
            console.log('Connected to:', bluetoothDevice.name);
        } else {
            console.log('Not connected to any Bluetooth device.');
        }
    }, [bluetoothDevice]);

    const sendScript = SendScript();
    const uploadBlockly = (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
      const element = document.getElementById('generatedCode');
      const script = "{" + element?.innerHTML?.replace(/<br>/g, '').replace(/&gt;/g, '>') + "}";
      e.preventDefault();
      sendScript.sendData(script);
    }


    return (
        <div className="top-bar">
            {/* <h2 className="logo">Littlearn</h2> */}

            <div className="input-container">


                <div className={`connect-button ${bluetoothDevice ? 'connected' : ''}`} onClick={bluetoothDevice ? handleDisconnect : handleConnect}>
                    {bluetoothDevice ? 'Connected' : 'Connect'}
                </div>

                <div className="send-data-button" onClick={handleSendData}>
                    Send
                </div>


            </div>

        </div>
    );
}

export default TopBar;
